import styled from "styled-components";

export const Wrapper = styled.div`
  display: flex;
  flex-direction: row;
  justify-content: center;
  width: 100%;
  gap: 10px;
  border-bottom: 1px solid #c0c0c0;
  padding-bottom: 18px;

  input{
    width: 50%;
    padding: 5px 10px;
  }

  button{
    padding: 0 15px;
    cursor: pointer;
    background-color: #4B0082;
    color: #F0F8FF;
    font-weight: 800;
  }
`;
