import styled from 'styled-components';

export const Wrapper = styled.div`
display: flex;
flex-grow: 1;
width: 42%;
flex-wrap: wrap;
flex-direction: column;
justify-content: flex-start;
align-items: flex-start;
border: 1px solid gray;
padding: 7px;
h2{
  font-size: 18px;
}
a, a:visited{
  text-decoration: none;
  color: #4682B4;
  font-weight: 500;
}
`;
