import React, { useState, createContext, useCallback } from 'react'
import api from '../services/api';
import avatar from '../assets/img/git.png';

export const GithubContext = createContext({
  loading: false,
  user: {},
  repositories: [],
  starred: [],
})

const GithubProvider = ({children}) => {

  const [ githubState, setGithubState ] = useState({
    hasUser: false,
    loading: false,
    user: {
      login: undefined,
      name: undefined,
      avatar_url: avatar,
      html_url: undefined,
      blog: undefined,
      company: undefined,
      location: undefined,
      followers: 0,
      following: 0,
      public_gists: 0,
      public_repos: 0,
    },
    repositories: [],
    starred: [],
  });

/** Função para buscar o usuário com suas informações */

const getUser = (username) => {
  setGithubState((prevState) => ({
    ...prevState,
    loading: !prevState.loading,
  }));

  api
  .get(`users/${username}`).then(({ data }) => {
    setGithubState((prevState) => ({
    ...prevState,
    hasUser: true,
    user: {
      login: data.login,
      name: data.name,
      avatar_url: data.avatar_url,
      html_url: data.html_url,
      blog: data.blog,
      company: data.company,
      location: data.location,
      followers: data.followers,
      following: data.following,
      public_gists: data.public_gists,
      public_repos: data.public_repos,
    },
    }));
  })
  .finally(() => {
    setGithubState((prevState) => ({
      ...prevState,
      loading: !prevState.loading,
    }));
  });
};

/** Função para buscar os repositórios */

const getUserRepos = () => {
  api.get(`users/${githubState.user.login}/repos`).then(({ data }) => {
    setGithubState((prevState) => ({
    ...prevState,
    repositories: data,
    }));
  })
};



  const contextValue = {
    githubState,
    getUser: useCallback((username) => getUser(username), []),
    getUserRepos: useCallback(() => getUserRepos(), [])
  }

  return (
    <GithubContext.Provider value={contextValue}>
      {children}
    </GithubContext.Provider>
  )
};

export default GithubProvider;
