import styled from "styled-components";

export const Wrapper = styled.div`
  display: flex;
  flex-direction: row;
  justify-content: center;
  width: 100%;
  gap: 10px;

  input{
    width: 50%;
    padding: 5px 10px;
  }

  button{
    padding: 0 10px;
    cursor: pointer;
  }
`;
