import React, { useState, createContext } from 'react'

export const GithubContext = createContext({
  user: {},
  repositories: [],
  starred: [],
})

const GithubProvider = ({children}) => {

  const [ githubState, setGithubState ] = useState({
    user: {
      login: undefined,
      name: undefined,
      avatar_url: 'https://avatars.githubusercontent.com/u/4985001?v=4',
      html_url: undefined,
      blog: undefined,
      company: undefined,
      followers: 0,
      following: 0,
      public_gists: 0,
      public_repos: 0,
    },
    repositories: [],
    starred: [],
  });

  const contextValue = {
    githubState,
  }

  return (
    <GithubContext.Provider value={contextValue}>
      {children}
    </GithubContext.Provider>
  )
};

export default GithubProvider;
